mapper.select:

		SELECT * FROM(  		
			SELECT rownum as rn, grade_code, user_code, game_code, grade_context, grade_regDate, grade_rate
			FROM grade_tbl
			where rownum <= #{pageNum} * #{amount}  and bookId = #{bookId}	
			order by regDate desc  	
  			)
  		where rn > (#{pageNum} -1) * #{amount}


SELECT count(*)
FROM grade_tbl 
WHERE game_code = #{game_code}
